[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "{{ '' }}"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<style>
    .quotation-container {
        font-family: Arial, sans-serif;
        font-size: 12px;
        padding: 20px;
    }
    .quotation-header {
        text-align: center;
        font-size: 18px;
        font-weight: bold;
        margin-bottom: 10px;
    }
    .company-details, .customer-details {
        width: 100%;
        border-collapse: collapse;
        margin-bottom: 10px;
    }
    .company-details td, .customer-details td {
        padding: 5px;
    }
    .quotation-table {
        width: 100%;
        border-collapse: collapse;
        margin-top: 10px;
    }
    .quotation-table th, .quotation-table td {
        border: 1px solid #000;
        padding: 8px;
        text-align: left;
    }
    .terms {
        font-size: 11px;
        margin-top: 20px;
    }
    .bank-details {
        font-size: 11px;
        margin-top: 15px;
        font-weight: bold;
    }
    .text-right {
        text-align: right;
    }
    .text-center {
        text-align: center;
    }
</style>

<div class="quotation-container">
    <div class="quotation-header">QUOTATION</div>
    {% set company = frappe.get_doc("Company", doc.company) %}
    <div class="quotation-header">TPIN: {{ company.tax_id or "N/A" }}</div>
    <table class="company-details">
        <tr>
            <td><strong>Company Name:</strong> VARUN BEVERAGES LIMITED</td>
            <td><strong>Contact Name:</strong> {{ doc.customer_name }}</td>
        </tr>
        <tr>
            <td><strong>Phone No:</strong> {{ doc.contact_phone or '-' }}</td>
            <td><strong>Email:</strong> {{ doc.contact_email or '-' }}</td>
        </tr>
        <tr>
            <td><strong>Date:</strong> {{ doc.transaction_date }}</td>
            <td><strong>Quote Validity:</strong> {{ doc.valid_till }}</td>
        </tr>
        <tr>
            <td><strong>Quote No:</strong> {{ doc.name }}</td>
        </tr>
    </table>

    <p>Dear {{ doc.customer_name }},</p>
    <p>With reference to your enquiry, we are pleased to enclose our proposal with the best possible prices for the following items. We hope you find it attractive and will place your valued order with us at your earliest.</p>
    <p>Thank you, and we assure you of our best cooperation & personalized services at all times. For any further information, please feel free to contact us.</p>

    <p><strong>Thanks & Regards,</strong><br>
    SHIVAY TECHNOLOGIES LIMITED</p>

    <table class="quotation-table">
        <thead>
            <tr>
                <th>Product Details</th>
                <th>Quantity</th>
                <th>List Price</th>
                <th>Amount</th>
            </tr>
        </thead>
        <tbody>
            {% for item in doc.items %}
            <tr>
                <td>{{ item.item_name }}</td>
                <td>{{ item.qty }}</td>
                <td>ZMW {{ "{:,.2f}".format(item.rate) }}</td>
                <td>ZMW {{ "{:,.2f}".format(item.amount) }}</td>
            </tr>
            {% endfor %}
        </tbody>
    </table>

    <table class="customer-details">
        <tr>
            <td><strong>Subtotal:</strong></td>
            <td class="text-right">ZMW {{ "{:,.2f}".format(doc.net_total) }}</td>
        </tr>
        <tr>
            <td><strong>VAT (16%):</strong></td>
            <td class="text-right">ZMW {{ "{:,.2f}".format(doc.total_taxes_and_charges) }}</td>
        </tr>
        <tr>
            <td><strong>Grand Total:</strong></td>
            <td class="text-right">ZMW {{ "{:,.2f}".format(doc.grand_total) }}</td>
        </tr>
    </table>

    <p class="terms"><strong>Terms & Conditions:</strong></p>
    <p class="terms">1. Issuance of this quotation does not bind us in any way to supply the said items unless a confirmed purchase order is received.</p>
    <p class="terms">2. The quoted price is valid for seven (7) days. Please reconfirm availability and price.</p>
    <p class="terms">3. Prices, specs, and availability are subject to change without prior notice.</p>

    <p class="bank-details">BANK DETAILS:<br>
    ACCOUNT NAME: SHIVAY TECHNOLOGY LTD | KWACHA A/C NO: 63071485932 | USD A/C NO: 63071486328<br>
    BRANCH NAME: CAIRO | BRANCH CODE: 260050 | SWIFT CODE: FIRNZMLX | CITY: LUSAKA | COUNTRY: ZAMBIA</p>
</div>
"}]
