css:
.main {
  background-image: url("/files/watermark.png");
  background-position: 0px 250px;
  line-height: 1;
  background-repeat: no-repeat;
  background-size: 800px 500px;
  background-attachment: fixed; /* Ensures it stays in place when printing */
}

.print-format {
 line-height: 1;
  position: relative;
  z-index: 1;
  margin-top: 20px;
  margin-left: 25px;
  margin-right: 25px;
  background-color: transparent;
}



.header, .footer {
  z-index: 2; /* Ensure header/footer are also above watermark */
  background-color: transparent; /* Prevent transparency issues */
}

.maintable {
  width: 100%;
  border-collapse: collapse;
}
th, td {
  border: 1px solid #ccc;
  padding: 6px;
  text-align: left;
}

.subtable {
  width: 215px;
  /*float:right;*/
  margin-left:500px;
  border-collapse: collapse;
}
.tablea{
  width: 48%;
  float:left;
  border-collapse: collapse;
}

.tableb{
  width: 48%;
  float:right;
  border-collapse: collapse;
}
